const fs=require('fs');

module.exports ={ 
	imgtobase64:function(path){
		var data=fs.readFileSync(path,'base64');
		return data;
	},
    base64toimg:function(base64string,format){
		var bitmap=new Buffer(base64string,'base64');
		fs.writeFileSync('example.'+format,bitmap);
		return 'example.'+format;
	}
}